import React from "react";
import {sensor_data} from "../fake_data/fake_data";
import Card from "@material-ui/core/Card";
import CardContent from "@material-ui/core/CardContent";
import Typography from "@material-ui/core/Typography";
import CardActions from "@material-ui/core/CardActions";
import Button from "@material-ui/core/Button";

import TextField from '@material-ui/core/TextField';
import SelectField from '@material-ui/core/SelectField';
import MenuItem from '@material-ui/core/MenuItem';
import Dialog from '@material-ui/core/Dialog';
import DialogActions from '@material-ui/core/DialogActions';
import DialogContent from '@material-ui/core/DialogContent';
import DialogContentText from '@material-ui/core/DialogContentText';
import DialogTitle from '@material-ui/core/DialogTitle';

class AlertSummaryCard extends React.Component {
    render() {

        // Deconstruct props.
        const { alert_name, alert_id, cb_remove_alert } = this.props;

        return (
            <div style={{paddingTop: '20px', width: '100%'}}>
                <Card style={{width: '100%', backgroundColor: '#EEEEEE'}}>
                  <CardContent style={{display: 'flex', flexDirection: 'column', alignItems: 'flex-start'}}>
                    <Typography variant="h5" component="h2">
                        {alert_name}
                    </Typography>
                  </CardContent>
                  <CardActions>
                    <Button size="small" onClick={() => cb_remove_alert(alert_id)}>Delete</Button>
                  </CardActions>
                </Card>
            </div>
        )
    }
}

class CreateAlertDialog extends React.Component {

    constructor() {
        super();
        this.state = {
            open: false,
            name: "",
            sensor: ""
        };

        this.handleClickOpen = this.handleClickOpen.bind(this);
        this.handleClose = this.handleClose.bind(this);
        this.handleCreate = this.handleCreate.bind(this);
        this.handleNameChange = this.handleNameChange.bind(this);
        this.handleSensorChange = this.handleSensorChange.bind(this);
    }

    handleClickOpen()  {
        this.setState((state) => {
            return {open: true}
        })
    }

    handleNameChange = (event) => {
        this.setState({
            name: event.target.value,
        });
    };

    handleSensorChange = (event) => {
        this.setState({
            sensor: event.target.value,
        });
    };

    handleClose() {
        this.setState((state) => {
            return {open: false, name: ""}
        })
    }

    handleCreate() {
        if (this.state.name !== "") {
            this.setState((state) => {
                return {open: false, name: ""}
            });
            this.props.cb_add_alert(this.state.name);
        }
    }

    render() {
        return (
            <div>
                <Button variant="outlined" color="primary" onClick={this.handleClickOpen}>
                    Create New Alert
                </Button>
                <Dialog open={this.state.open} onClose={this.handleClose} aria-labelledby="form-dialog-title">
                    <DialogTitle id="form-dialog-title">Create Alert</DialogTitle>
                    <DialogContent>
                        <TextField
                            autoFocus
                            margin="dense"
                            id="name"
                            label="Alert Name"
                            value={this.state.name}
                            onChange={this.handleNameChange}
                            type="email"
                            fullWidth
                        />
                        <SelectField
                            floatingLabelText="Frequency"
                            value={this.state.sensor}
                            onChange={this.handleSensorChange}
                        >
                            <MenuItem value={1} primaryText="Never" />
                            <MenuItem value={2} primaryText="Every Night" />
                            <MenuItem value={3} primaryText="Weeknights" />
                            <MenuItem value={4} primaryText="Weekends" />
                            <MenuItem value={5} primaryText="Weekly" />
                        </SelectField>
                    </DialogContent>
                    <DialogActions>
                        <Button onClick={this.handleClose} color="primary">
                            Cancel
                        </Button>
                        <Button onClick={this.handleCreate} color="primary">
                            Create
                        </Button>
                    </DialogActions>
                </Dialog>
            </div>
        )
    }
}

export { AlertSummaryCard, CreateAlertDialog };
