import React, { useState } from 'react';
import './App.css';
import mapboxgl from 'mapbox-gl';
import Tabs from 'react-bootstrap/Tabs'
import Tab from 'react-bootstrap/Tab'
import ReactMapGL, {Marker, Popup, NavigationControl, FullscreenControl, ScaleControl} from 'react-map-gl';

import sensor_icon from './icons/sensor.png';
import camera_trap_icon from './icons/camera_trap.png';

const SENSORS = [
    {"type": "Sensor Tower", "latitude":51.5094, "longitude":-2.5890, "id": 1, "icon": sensor_icon},
    {"type": "Sensor Tower","latitude":51.5100,"longitude":-2.5957, "id": 2, "icon": sensor_icon},
    {"type": "Camera Trap","latitude":51.5110,"longitude":-2.5917, "id": 1, "icon": camera_trap_icon}
];

const ICON_SIZE = 80;


class Sensors extends React.Component {
  render() {
    const {data, onClick} = this.props;

    return data.map((sensor, index) => (
      <Marker key={`marker-${index}`} longitude={sensor.longitude} latitude={sensor.latitude}>
          <div style={{cursor: 'pointer'}} onClick={() => onClick(sensor)}>
             <img src={sensor.icon}
                  style={{
                      width: `${ICON_SIZE}px`,
                      transform: `translate(${-ICON_SIZE / 2}px,${-ICON_SIZE}px)`}}/>
          </div>
      </Marker>
    ));
  }
}

class SensorInfo extends React.Component {
  render() {
    const {info} = this.props;

    return (
      <div style={{paddingTop: "15px", textAlign: "left"}}>
          <div><b>{`${info.type}: ${info.id}`}</b></div>
          {(info.type === "Camera Trap")
              ? <div>
                  <div style={{cursor: 'pointer'}}><u>Upload Images</u></div>
                  <div style={{cursor: 'pointer'}}><u>Details</u></div>
                </div>
              : <div>
                  <div>Status: <b style={{color: '#55BB55'}}>Live</b></div>
                  <div style={{cursor: 'pointer'}}><u>Details</u></div>
                </div>
          }
          <div style={{paddingTop: "15px", cursor: 'pointer', color: '#FF6666'}}><u>Delete Sensor</u></div>
      </div>
    );
  }
}

class Map extends React.Component {

  state = {
    viewport: {
      width: "100%",
      height: 1000,
      latitude: 51.5084,
      longitude: -2.5927,
      zoom: 16
    }
  };

  _updateViewport = viewport => {
    this.setState({viewport});
  };

  _onClickMarker = city => {
    this.setState({popupInfo: city});
  };

  _renderPopup() {
    const {popupInfo} = this.state;

    return (
      popupInfo && (
        <Popup
          tipSize={5}
          anchor="top"
          longitude={popupInfo.longitude}
          latitude={popupInfo.latitude}
          closeOnClick={false}
          onClose={() => this.setState({popupInfo: null})}
        >
          <SensorInfo info={popupInfo} />
        </Popup>
      )
    );
  }

  render() {
    return (
      <ReactMapGL
        {...this.state.viewport}
        onViewportChange={(viewport) => this.setState({viewport})}
        mapStyle='mapbox://styles/hturner30/ck62e8klf0xzn1ithmmtve3iq'
        mapboxApiAccessToken='pk.eyJ1IjoiaHR1cm5lcjMwIiwiYSI6ImNrNjJhZWFkdjBiNnMzbm1tNHh1cDNlMWsifQ.0ZiYTbXUCbaJ2mxlibJjDg'
      >
          {(this.state.viewport.zoom >= 15)
              ? <Sensors data={SENSORS} onClick={this._onClickMarker}/>
              : null
          }
          {this._renderPopup()}
      </ReactMapGL>
    );
  }
}


class TabBoard extends React.Component {

    render() {
        let { tabs } = this.props;
        return (
            <Tabs defaultActiveKey="profile" id="uncontrolled-tab-example">
                <Tab eventKey="dashboard" title="Dashboard">
                    <div>Tab 1</div>
                </Tab>
                <Tab eventKey="network" title="Sensor Network">
                    <div>Tab 2</div>
                </Tab>
            </Tabs>
        )
    }
}

class App extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            tabs: [{"eventKey": "camera_trap", "title": "Camera Trap 1", "id": 1}]
        }
    }

    render() {
        return (
            <main className="App">
                <Map/>
                <TabBoard tabs={this.state.tabs}/>
            </main>
        )
    }
}

export default App;
