import React from "react";
import {Marker} from "react-map-gl";
import { sensor_data, detection_data } from "../fake_data/sensor_data";
import Tooltip from '@material-ui/core/Tooltip';
import { DeleteSensorButton, EditSensorLocationButton } from "./buttons";
import CloseIcon from "@material-ui/icons/Close";
import DeleteIcon from '@material-ui/icons/Delete';
import EditLocationIcon from '@material-ui/icons/EditLocation';


import IconButton from "@material-ui/core/IconButton";
import MediaCard from "./mediaCard";
import ChartCard from "./chartCard";

// Some global settings.
const ICON_SIZE = 80;

class SensorIcon extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            drag: false
        }
    }

    get_pin (sensor, onClick) {
        if (this.state.drag === true) {

        }
        else {
            return (
                <div style={{cursor: 'pointer'}} onClick={() => onClick(sensor)}>
                    <img src={sensor.icon} alt="sensor_icon"
                         style={{
                             width: `${ICON_SIZE}px`,
                             transform: `translate(${-ICON_SIZE / 2}px,${-ICON_SIZE}px)`}}
                    />
                </div>
            )
        }
    }

    render() {

        const {data, onClick, onMarkerDragEnd} = this.props;

        return data.map((sensor, index) => (
            <Marker
                key={`marker-${index}`}
                draggable
                onDragEnd={onMarkerDragEnd}
                longitude={sensor.longitude}
                latitude={sensor.latitude}>
                {this.get_pin(sensor, onClick)}
            </Marker>
        ));
    }
}

//<div style={{cursor: 'pointer'}} onClick={() => onClick(sensor)}>
//             <img src={sensor.icon}
//                  alt="sensor_icon"
//                  style={{
//                      width: `${ICON_SIZE}px`,
//                      transform: `translate(${-ICON_SIZE / 2}px,${-ICON_SIZE}px)`}}/>
//          </div>

class InfoCard extends React.Component {

    render() {
        // Deconstruct props.
        const { sensor_name } = this.props;

        // Find the sensor with the right name.
        const sensor = sensor_data.filter(sensor => sensor.name === sensor_name)[0];

        return (
            <div style={{padding: "20px", paddingTop: "0px"}}>
                <div style={{padding: "20px", backgroundColor:"#E4F3D8", display: "flex", flexDirection: "column", alignItems: "flex-start"}}>
                    <div><h4>Basic Sensor Details</h4></div>
                    <div><b>Name:</b> {sensor.name}</div>
                    <div><b>Sensor Type:</b> {sensor.type}</div>
                    <div><b>Created:</b> {sensor.created}</div>
                    <div><b>Data Update Frequency:</b> {sensor.frequency}</div>
                    <div><b>Sensor Security:</b> {sensor.access}</div>
                    <div><b>Data Provenance:</b> {sensor.provenance}</div>
                </div>
            </div>
        );
    }
}

class TitleCard extends React.Component {

    render() {
        // Deconstruct props.
        const { sensor_name, remove_tab_cb, tab_id } = this.props;

        // Find the sensor with the right name.
        const sensor = sensor_data.filter(sensor => sensor.name === sensor_name)[0];

        return (
            <div style={{padding: "20px", display: "flex"}}>
                <Tooltip title="Close">
                    <IconButton onClick={() => remove_tab_cb(tab_id)}>
                        <CloseIcon></CloseIcon>
                    </IconButton>
                </Tooltip>
                <h3 style={{marginTop:"6px", marginLeft:"10px"}}>{sensor.name}</h3>
            </div>
        );
    }
}

class DataCard extends React.Component {

    render() {
        // Deconstruct props.
        const { sensor_name } = this.props;

        // Find the sensor with the right name.
        const sensor = sensor_data.filter(sensor => sensor.name === sensor_name)[0];

        // Get detction data.
        const data = detection_data.filter(data => data.sensor_id === sensor.id);

        // Compute the card content based on the detection data.
        let card_content = <div>No data available yet.</div>;
        if (data.length > 0) {
            if (data[0].type === 'image') {
                card_content = <div style={{display: 'flex'}}>
                {data.map((d) =>
                    <div style={{padding: '10px'}}>
                        <MediaCard
                            image={d.image}
                            title={d.title}
                            time_seen={d.time_seen}
                        />
                    </div>
                )}
                </div>
            }
            else {
                card_content = <div style={{display: 'flex'}}>
                {data.map((d) =>
                    <div>
                        <ChartCard/>
                    </div>
                )}
                </div>
            }
        }

        return (
            <div style={{padding: "20px", paddingTop: "0px"}}>
                <div style={{padding: "20px", backgroundColor:"#CAE6B2", display: "flex", flexDirection: "column", alignItems: "flex-start"}}>
                    <div><h4>Data</h4></div>
                    {card_content}
                </div>
            </div>
        );
    }
}

class ControlCard extends React.Component {

    render() {
        // Deconstruct props.
        const { sensor_name, callbackRemoveSensor, tab_id } = this.props;

        // Find the sensor with the right name.
        const sensor = sensor_data.filter(sensor => sensor.name === sensor_name)[0];

        return (
            <div style={{padding: "20px", paddingTop: "0px"}}>
                <div style={{padding: "20px", backgroundColor:"#AFD88D", display: "flex", flexDirection: "column", alignItems: "flex-start"}}>
                    <div><h4>Control</h4></div>
                    <div style={{display: 'flex'}}>
                        <EditSensorLocationButton></EditSensorLocationButton>
                        <DeleteSensorButton
                            sensorID={sensor.id}
                            callbackRemoveSensor={callbackRemoveSensor}
                            tab_id={tab_id}
                        />
                    </div>
                </div>
            </div>
        );
    }
}

class SensorBite extends React.Component {
  render() {

      // Deconstruct props.
      const {info, add_tab_cb} = this.props;

      return (
          <div style={{paddingTop: "15px", textAlign: "left"}}>
              <div><b>{`${info.name}`}</b></div>
              <div>
                  <div>{`Type: ${info.type}`}</div>
                  <div>Health: <b style={{color: '#55BB55'}}>{info.health}</b></div>
                  <div onClick={() => add_tab_cb(info.name)} style={{cursor: 'pointer'}}><u>More Details</u></div>
              </div>
          </div>
      );
  }
}

export { SensorBite, SensorIcon, InfoCard, TitleCard, DataCard, ControlCard };
