import React from "react";
import {sensor_data} from "../fake_data/fake_data";
import Card from "@material-ui/core/Card";
import CardContent from "@material-ui/core/CardContent";
import Typography from "@material-ui/core/Typography";
import CardActions from "@material-ui/core/CardActions";
import Button from "@material-ui/core/Button";

class AlertSummaryCard extends React.Component {
    render() {

        // Deconstruct props.
        const { sensor_name, add_tab_cb } = this.props;

        // Find the sensor with the right name.
        const sensor = fake_data.filter(sensor => sensor.name === sensor_name)[0];

        return (
            <div style={{paddingTop: '20px', width: '100%'}}>
                <Card style={{width: '100%', backgroundColor: '#EEEEEE'}}>
                  <CardContent style={{display: 'flex', flexDirection: 'column', alignItems: 'flex-start'}}>
                    <Typography variant="h5" component="h2">
                        {sensor.name}
                    </Typography>
                    <Typography color="textSecondary">
                        {sensor.type}
                    </Typography>
                  </CardContent>
                  <CardActions>
                    <Button size="small" onClick={() => add_tab_cb(sensor.name)}>Details</Button>
                  </CardActions>
                </Card>
            </div>
        )
    }
}

export { AlertSummaryCard };
