import React, { useCallback } from 'react';
import './App.css';
import mapboxgl from 'mapbox-gl';

class Map extends React.Component {

    constructor(props) {
        super(props);
        mapboxgl.accessToken = 'pk.eyJ1IjoiaHR1cm5lcjMwIiwiYSI6ImNrNjJhZWFkdjBiNnMzbm1tNHh1cDNlMWsifQ.0ZiYTbXUCbaJ2mxlibJjDg';
        this.state = {
            lng: -2.5947,
            lat: 51.5079,
            zoom: 16
        };
    }

    makeMarker() {
        const el = document.createElement('div');
        el.style.backgroundImage = 'https://i.stack.imgur.com/kEvED.png'

    }

  componentDidMount() {
        this.map = new mapboxgl.Map({
            container: this.mapContainer,
            style: 'mapbox://styles/mapbox/outdoors-v11',
            center: [this.state.lng, this.state.lat],
            zoom: this.state.zoom
        });

        const markerOptions = {
            element: this.makeMarker(),
            color: '#FFFFFF'
        }
        const marker = new mapboxgl.Marker(markerOptions)
          .setLngLat([-2.5947, 51.5079])
          .addTo(this.map);
        }

  componentWillUnmount() {
    this.map.remove();
  }

  render() {
    const style = {
      position: 'absolute',
      top: 0,
      bottom: 0,
      width: '100%'
    };

    return <div style={style} ref={el => this.mapContainer = el} />;
  }
}

function App() {

  // We pass onDrop function and accept prop to the component. It will be used as initial params for useDropzone hook
  return (
    <main className="App">
        <Map/>
    </main>
  );
}

export default App;
