import React from "react";
import {sensor_data} from "../fake_data/fake_data";
import Card from "@material-ui/core/Card";
import CardContent from "@material-ui/core/CardContent";
import Typography from "@material-ui/core/Typography";
import CardActions from "@material-ui/core/CardActions";
import Button from "@material-ui/core/Button";

import TextField from '@material-ui/core/TextField';
import Dialog from '@material-ui/core/Dialog';
import DialogActions from '@material-ui/core/DialogActions';
import DialogContent from '@material-ui/core/DialogContent';
import DialogContentText from '@material-ui/core/DialogContentText';
import DialogTitle from '@material-ui/core/DialogTitle';

class AlertSummaryCard extends React.Component {
    render() {

        // Deconstruct props.
        const { alert_name, alert_id, cb_remove_alert } = this.props;

        return (
            <div style={{paddingTop: '20px', width: '100%'}}>
                <Card style={{width: '100%', backgroundColor: '#EEEEEE'}}>
                  <CardContent style={{display: 'flex', flexDirection: 'column', alignItems: 'flex-start'}}>
                    <Typography variant="h5" component="h2">
                        {alert_name}
                    </Typography>
                  </CardContent>
                  <CardActions>
                    <Button size="small" onClick={() => cb_remove_alert(alert_id)}>Delete</Button>
                  </CardActions>
                </Card>
            </div>
        )
    }
}

class CreateAlertDialog extends React.Component {

    constructor() {
        super();
        this.state = {
            open: false
        };

        this.handleClickOpen = this.handleClickOpen.bind(this);
        this.handleClose = this.handleClose.bind(this);
    }

    handleClickOpen()  {
        this.setState((state) => {
            return {open: true}
        })
    }

    handleClose() {
        this.setState((state) => {
            return {open: false}
        })
    }

    handleCreate() {
        this.setState((state) => {
            return {open: false}
        });
        this.props.handleClose("test");
    }

    render() {
        return (
            <div>
                <Button variant="outlined" color="primary" onClick={this.handleClickOpen}>
                    Create New Alert
                </Button>
                <Dialog open={this.state.open} onClose={this.handleClose} aria-labelledby="form-dialog-title">
                    <DialogTitle id="form-dialog-title">Create Alert</DialogTitle>
                    <DialogContent>
                        <TextField
                            autoFocus
                            margin="dense"
                            id="name"
                            label="Give the alert a name."
                            type="email"
                            fullWidth
                        />
                    </DialogContent>
                    <DialogActions>
                        <Button onClick={this.handleClose} color="primary">
                            Cancel
                        </Button>
                        <Button onClick={this.handleCreate} color="primary">
                            Create
                        </Button>
                    </DialogActions>
                </Dialog>
            </div>
        )
    }
}

export { AlertSummaryCard, CreateAlertDialog };
