import React from 'react';
import Chart from 'react-apexcharts'

class ChartCard extends React.Component {
	constructor(props) {
		super(props);

		this.state = {
			options: {
				chart: {
					id: props.title,
					type: 'line'
				},
				xaxis: {
					categories: []
				},
				stroke: {
					curve: 'smooth',
					width: 1
				},
				tooltip: {
					marker: { show: false }
				}
			},
			series: [{
				name: 'series-1',
				data: []
			}]
		}
	}

	componentDidMount() {
		fetch(this.props.api)
			.then(res => res.json())
			.then(
				(result) => {
					let data = [];
					let categories = [];
					result.records.forEach((item, index) => {
						const value = item.fields.pm10;
						const category = item.fields.date.split('T')[0];
						data.push(value);
						categories.push(category);
					});

					// Update the state.
					this.setState({
						series: [{
							name: 'series-1',
							data: []
						}]
					});

				},
				(error) => {

				}
			)
	}

	render() {
		return (
			<Chart options={this.state.options} series={this.state.series} type="line" width='100%' height={320} />
			)
	}
}

export default ChartCard;
