import React, { useState } from 'react';
import './App.css';
import mapboxgl from 'mapbox-gl';
import ReactMapGL, {Marker} from 'react-map-gl';

class Pins extends React.Component {
  render() {
    const {data, onClick} = this.props;

    return data.map((city, index) => (
      <Marker key={`marker-${index}`} longitude={city.longitude} latitude={city.latitude}>
        <svg
          height={SIZE}
          viewBox="0 0 24 24"
          style={{
            cursor: 'pointer',
            fill: '#d00',
            stroke: 'none',
            transform: `translate(${-SIZE / 2}px,${-SIZE}px)`
          }}
          onClick={() => onClick(city)}
        >
          <path d={ICON} />
        </svg>
      </Marker>
    ));
  }
}

class Map extends React.Component {

  state = {
    viewport: {
      width: "100%",
      height: 1000,
      latitude: 51.5084,
      longitude: -2.5927,
      zoom: 16
    }
  };

  render() {
    return (
      <ReactMapGL
        {...this.state.viewport}
        onViewportChange={(viewport) => this.setState({viewport})}
        mapStyle='mapbox://styles/mapbox/outdoors-v11'
        mapboxApiAccessToken='pk.eyJ1IjoiaHR1cm5lcjMwIiwiYSI6ImNrNjJhZWFkdjBiNnMzbm1tNHh1cDNlMWsifQ.0ZiYTbXUCbaJ2mxlibJjDg'
      >
          <Marker latitude={51.5084} longitude={-2.5927} offsetLeft={-20} offsetTop={-10}>
            <img src='https://caito.emu.ee/wp-content/uploads/2016/12/map-marker-3-xxl.png' alt="Icon" width='50px'/>
          </Marker>
      </ReactMapGL>
    );
  }
}

function App() {

  // We pass onDrop function and accept prop to the component. It will be used as initial params for useDropzone hook
  return (
    <main className="App">
        <Map/>
    </main>
  );
}

export default App;
