import React, { useState } from 'react';
import './App.css';
import mapboxgl from 'mapbox-gl';
import ReactMapGL, {Marker, Popup, NavigationControl, FullscreenControl, ScaleControl} from 'react-map-gl';

const CITIES = [
    {"species":"Badger","image":"https://ichef.bbci.co.uk/images/ic/640xn/p07cyc06.jpg","latitude":51.5094,"longitude":-2.5927, "icon": "https://img.icons8.com/color/48/000000/badger.png"},
    {"species":"Cat","image":"https://www.getoutwiththekids.co.uk/wp-content/uploads/2013/10/Fox-caught-on-camera-trap-from-Nature-Spy.jpeg","latitude":51.5094,"longitude":-2.5957, "icon": "https://img.icons8.com/color/48/000000/fox.png"},
]

class Pins extends React.Component {
  render() {
    const {data, onClick} = this.props;

    return data.map((city, index) => (
      <Marker key={`marker-${index}`} longitude={city.longitude} latitude={city.latitude}>
         <img src={city.icon}
              alt={city.species}
              style={{cursor: 'pointer'}}
              onClick={() => onClick(city)}/>
      </Marker>
    ));
  }
}

class CityInfo extends React.Component {
  render() {
    const {info} = this.props;
    const displayName = `${info.species}`;

    return (
      <div>
          <div>
              {displayName}
          </div>
          <div>
              Last Seen: 4 hours ago
          </div>
        <img width={240} src={info.image} />
      </div>
    );
  }
}

class Map extends React.Component {

  state = {
    viewport: {
      width: "100%",
      height: 1000,
      latitude: 51.5084,
      longitude: -2.5927,
      zoom: 16
    }
  };

  _updateViewport = viewport => {
    this.setState({viewport});
  };

  _onClickMarker = city => {
    this.setState({popupInfo: city});
  };

  _renderPopup() {
    const {popupInfo} = this.state;

    return (
      popupInfo && (
        <Popup
          tipSize={5}
          anchor="top"
          longitude={popupInfo.longitude}
          latitude={popupInfo.latitude}
          closeOnClick={false}
          onClose={() => this.setState({popupInfo: null})}
        >
          <CityInfo info={popupInfo} />
        </Popup>
      )
    );
  }

  render() {
    return (
      <ReactMapGL
        {...this.state.viewport}
        onViewportChange={(viewport) => this.setState({viewport})}
        mapStyle='mapbox://styles/mapbox/outdoors-v11'
        mapboxApiAccessToken='pk.eyJ1IjoiaHR1cm5lcjMwIiwiYSI6ImNrNjJhZWFkdjBiNnMzbm1tNHh1cDNlMWsifQ.0ZiYTbXUCbaJ2mxlibJjDg'
      >
          <Pins data={CITIES} onClick={this._onClickMarker} />
          {this._renderPopup()}
      </ReactMapGL>
    );
  }
}

function App() {

  // We pass onDrop function and accept prop to the component. It will be used as initial params for useDropzone hook
  return (
    <main className="App">
        <Map/>
    </main>
  );
}

export default App;
