import React, { useState } from 'react';
import './App.css';
import mapboxgl from 'mapbox-gl';
import Tabs from 'react-bootstrap/Tabs'
import Tab from 'react-bootstrap/Tab'
import ReactMapGL, {Marker, Popup, NavigationControl, FullscreenControl, ScaleControl} from 'react-map-gl';

// Import custom components.
import { Sensor, SensorIcon } from './components/sensor';

// Import our fake data.
import SENSORS from "./fake_data/sensors";



class Map extends React.Component {

  state = {
    viewport: {
      width: "100%",
      height: 1000,
      latitude: 51.5084,
      longitude: -2.5927,
      zoom: 16
    }
  };

  _updateViewport = viewport => {
    this.setState({viewport});
  };

  _onClickMarker = city => {
    this.setState({popupInfo: city});
  };

  _renderPopup() {
    const {popupInfo} = this.state;

    return (
      popupInfo && (
        <Popup
          tipSize={5}
          anchor="top"
          longitude={popupInfo.longitude}
          latitude={popupInfo.latitude}
          closeOnClick={false}
          onClose={() => this.setState({popupInfo: null})}
        >
          <SensorInfo info={popupInfo} />
        </Popup>
      )
    );
  }

  render() {
    return (
      <ReactMapGL
        {...this.state.viewport}
        onViewportChange={(viewport) => this.setState({viewport})}
        mapStyle='mapbox://styles/hturner30/ck62e8klf0xzn1ithmmtve3iq'
        mapboxApiAccessToken='pk.eyJ1IjoiaHR1cm5lcjMwIiwiYSI6ImNrNjJhZWFkdjBiNnMzbm1tNHh1cDNlMWsifQ.0ZiYTbXUCbaJ2mxlibJjDg'
      >
          {(this.state.viewport.zoom >= 15)
              ? <SensorIcon data={SENSORS} onClick={this._onClickMarker}/>
              : null
          }
          {this._renderPopup()}
      </ReactMapGL>
    );
  }
}


class TabBoard extends React.Component {

    render() {
        let { tabs, remove_tab_cb } = this.props;
        return (
            <Tabs defaultActiveKey="profile" id="uncontrolled-tab-example">
                <Tab eventKey="dashboard" title="Dashboard">
                    <div>Tab 1</div>
                </Tab>
                <Tab eventKey="network" title="Sensor Network">
                    <div>Tab 2</div>
                </Tab>
                {tabs.map(function(tab){
                    return (
                        <Tab eventKey={tab.eventKey} title={tab.title}>
                            <div onClick={() => remove_tab_cb(tab.id)} style={{cursor: "pointer"}}><u>Close</u></div>
                        </Tab>
                    )
                })}
            </Tabs>
        )
    }
}

class App extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            tabs: [{"eventKey": "camera_trap", "title": "Camera Trap 1", "id": 1}]
        }
        this.removeTab = this.removeTab.bind(this)
    }

    removeTab (id) {
        this.setState((state) => {
            const new_tabs = state.tabs.filter(tab => tab.id !== id);
            return {tabs: new_tabs}
        })
    }

    render() {
        return (
            <main className="App">
                <Map/>
                <TabBoard
                    tabs={this.state.tabs}
                    remove_tab_cb={this.removeTab}
                />
            </main>
        )
    }
}

export default App;
